var menustatus;
var menuVisible; 

//display

function initz(){
	//true
	if (menustatus && menuVisible) {

		lockCheck.visible = true;
		this.visible = true;
		this.x = 0;
		trace("true:Initz"+"  "+this.x);

}
		else
	{
		//false
			
		lockCheck.visible = false;
		this.visible = false;
		this.x = -140;
		trace("false:Initz"+" "+this.x);
	}// end else if
	
}
initz();
//mouse slide
function checkPos() {
	if (menustatus) {
		//true
		if (MovieClip(root).mouseX<141) {
			//slideMenu(0,true);
			 menuVisible = true;
			trace("True:checkPos"+"  "+this.x);
			trace(MovieClip(root).mouseX);
			//trace(slideMenu);
		}
		//false
		else{
			this.visible = false;
			//slideMenu(-140,false);
			menustatus = false;
			MovieClip(root).menucontrol.visible = true;
			menuVisible = false;
			trace("False:checkPos"+" "+this.x);
			trace(MovieClip(root).mouseX);
			
			}
		}// end if
	// end else if  
}// End of the function

var i:int = 1;

while (i<=menu_names.length) {
	this["b"+i].butt.enabled = true;
	this["b"+i].tf.textColor = this.myrolloutcolor;
	this["b"+i].tf.text = menu_names[i];
this["b"+i].butt.addEventListener(MouseEvent.CLICK,jumptopage(this["b"+i].targetLabel));


function jumptopage(targetLabel:String):Function{
return function (event:MouseEvent):void{
MovieClip(root).gotoAndPlay(targetLabel);
	if(targetLabel == targetLabel){
	this["b"+i].butt.enabled = false
	}
	


}; 
};	

 ++i;


}// end while


var j:int = 0;
