	this["b"+this.disabledcontainer[j]].butt.enabled = false;
	this["b"+this.disabledcontainer[j]].tf.textColor = this.myhovercolor;
	++j;	

}// end while


//slide menu 
function slideMenu(val:uint,bol:Boolean ){
this.addEventListener(Event.ENTER_FRAME,Enter);
function Enter(e:Event):void{
	trace(this.x);
	this.x = this.x+(val-this.x)/3;	
		trace(this.x);
	trace("diri");
	trace(val+"  " +bol);
		if (this.x>=val+1 && this.x<=val-1) {
			trace("slideMenu"+"Working"+this.x);
			this.x = val;
			this.visible = bol;	
			
	//this.removeEventListener(Event.ENTER_FRAME, Enter);

		}

		// end if  
		trace(val+"  " +bol);
		
	};
}// End of the function

