import flash.events.Event;



var lastFrame:int = 1;

btn_t2.addEventListener(MouseEvent.CLICK, reverse2);
btn_t3.addEventListener(MouseEvent.CLICK, but3);
/*
btn_but2.height = 25;
btn_but2.width = 25;
btn_but2.x = btn_but2.x;

btn_but3.height = 25;
btn_but3.width = 25;
btn_but3.x = btn_but3.x+5;
*/

function reverse2(event:Event):void {
    addEventListener(Event.ENTER_FRAME, rev2);
	
}

function rev2(e:Event):void
{
      prevFrame();
      if(currentFrame == lastFrame)
      {
		  gotoAndStop(1);
   removeEventListener(Event.ENTER_FRAME, rev2);
	
      }
}
//this.onEnterFrame = undefined;

